<script setup>
import { computed } from "vue";

import { ArrowTopRightOnSquareIcon } from "@heroicons/vue/24/outline";
import { useForm } from "@inertiajs/vue3";
import { useAccount, useChainId } from "@wagmi/vue";
import { ShieldCheckIcon } from "lucide-vue-next";

import BaseButton from "@/Components/BaseButton.vue";
import ChainSymbol from "@/Components/ChainSymbol.vue";
import Loading from "@/Components/Loading.vue";
import PrimaryButton from "@/Components/PrimaryButton.vue";
import TxStatus from "@/Components/TxStatus.vue";
import { useContractFees, useReactiveContractCall } from "@/hooks/useContractCall";
import AppLayout from "@/Layouts/AppLayout.vue";
import Web3Auth from "@/Pages/Auth/Web3Auth.vue";

const props = defineProps({
    nfts: Object,
});

const chainId = useChainId();
const nft = computed(() => props.nfts[chainId.value]);
const nftId = computed(() => nft.value?.id);
const { address } = useAccount();
// Form for verification
const verifyForm = useForm({
    nft_id: nftId,
});

// Contract interaction state
const abi = computed(() => nft.value?.abi);
const contract = computed(() => nft.value?.contract);
const state = useReactiveContractCall(abi, contract);

// Get minting fees if any
const { fees, feesFormatted } = useContractFees(
    abi,
    contract,
    "mintPrice"
);

// Handle NFT minting
const mint = async () => {
    await state.call("safeMint", [address.value], fees.value);
    if (state.error) return;
    // After successful minting, verify the user
    verify();
};

// Handle manual verification
const verify = () => {
    verifyForm.post(window.route("nfts.verify", { nft: nftId.value }), {
        preserveState: true,
        preserveScroll: true,
    });
};

</script>

<template>

    <Head title="KYC NFT Minting" />
    <AppLayout>
        <div class="card max-w-3xl w-full mb-6 mx-auto dark:bg-gray-850 sm:p-12 sm:!pt-6 h-full border-0 card-border">
            <div class="card-body card-gutterless h-full">
                <div class="flex items-center justify-between mb-5">
                    <h3 class=" w0 flex items-center gap-2">
                        <ShieldCheckIcon class="w-7 h-7 stroke-[0.7] text-sky-400" />
                        {{ $t("Mint KYC NFT") }}

                    </h3>

                </div>

                <div class="grid gap-6">
                    <!-- NFT Information -->
                    <div class="border p-6 border-gray-650 bg-gray-750/50 rounded-lg">
                        <div class="mb-4 flex justify-between">
                            <h4 class="text-lg  !text-primary font-medium">
                                {{ $t("KYC Membership NFT") }}
                            </h4>
                            <div class="flex items-center space-x-2">
                                <div :class="[
                                    'w-3 h-3 rounded-full',
                                    $page.props.auth.user.hasVerifiedAddress ? 'bg-green-500' : 'bg-gray-500'
                                ]"></div>
                                <span>{{ $t("Address Verification") }}</span>
                            </div>
                        </div>
                        <div class="space-y-2">
                            <p class="text-gray-300">
                                {{ $t("This NFT represents your KYC verification status on the platform.") }}
                            </p>
                        </div>
                        <div
                            v-if="$page.props.auth.user"
                            class="pt-5"
                        >
                            <div
                                v-if="!$page.props.auth.user.hasVerifiedAddress"
                                class="flex flex-col sm:flex-row items-center gap-3 justify-end"
                            >
                                <TxStatus
                                    class="w-full sm:w-[unset]"
                                    :state="state"
                                />
                                <!-- Mint Button -->
                                <PrimaryButton
                                    class="w-full sm:w-[unset]"
                                    @click="mint"
                                    :disabled="state.busy || verifyForm.processing"
                                >
                                    <Loading
                                        class="mr-2 -ml-1 inline-block w-5 h-5"
                                        v-if="state.busy"
                                    />
                                    <span>
                                        {{ $t("Mint KYC NFT") }}
                                        {{ feesFormatted }}
                                    </span>
                                    <ChainSymbol
                                        class="ml-1"
                                        :chain-id="chainId"
                                    />
                                </PrimaryButton>
                            </div>
                        </div>
                        <div
                            v-else
                            class="pt-5"
                        >
                            <div class="flex flex-col sm:flex-row items-center gap-3 justify-end">
                                <Web3Auth />
                            </div>
                        </div>
                    </div>
                    <div class="border p-6 border-gray-650 bg-gray-750/50 rounded-lg">
                        <div class="mb-4 flex items-center justify-between">
                            <div>
                                <img
                                    height="64"
                                    class="w-32 h-auto inline-flex mr-2"
                                    src="data:image/png;base64,"
                                >
                            </div>
                            <div class="flex items-center space-x-2">
                                <div :class="[
                                    'w-3 h-3 rounded-full',
                                    $page.props.auth.user.hasBab ? 'bg-green-500' : 'bg-gray-500'
                                ]"></div>
                                <span>{{ $t("BAB Verification") }}</span>
                            </div>
                        </div>
                        <div class="space-y-2">
                            <p class=" !text-gray-400 font-medium">
                                {{ $t("Binance Exchange Soul bound token") }}
                            </p>
                            <p class="text-gray-300">
                                To complete KYC you need to mint the Binance (Exchange) account bound (BAB) token
                            </p>
                            <div class="pt-3 flex justify-end">
                                <BaseButton
                                    v-if="!$page.props.auth.user.hasBab"
                                    class="w-full sm:w-[unset]"
                                    size="xs"
                                    outlined
                                    secondary
                                    url
                                    target="_blank"
                                    href="https://www.binance.com/en/BABT"
                                >
                                    <span>{{ $t("Mint the Binance KYC NFT") }}</span>
                                    <ArrowTopRightOnSquareIcon class="w-4 h-4 ml-2" />
                                </BaseButton>
                            </div>
                        </div>

                    </div>
                    <!-- Action Buttons -->
                    <div
                        v-if="$page.props.auth.user"
                        class="pt-3 flex justify-end"
                    >
                        <PrimaryButton
                            v-if="!$page.props.auth.user.hasKyc"
                            class="w-full sm:w-[unset]"
                            @click="verify"
                            size="xs"
                            outlined
                            :disabled="verifyForm.processing"
                        >
                            <Loading
                                class="mr-2 -ml-1 inline-block w-5 h-5"
                                v-if="verifyForm.processing"
                            />
                            <span>{{ $t("Update Verification Status") }}</span>
                        </PrimaryButton>
                    </div>
                </div>
            </div>
        </div>
    </AppLayout>
</template>